mapper.update:
update product
name = #{name}, price = #{price }, category_id = #{categoryId}, video = #{video}, image = #{image}, source = #{source }, description = #{description}, update_time = now(), status = 0
id = #{id}

mapper.select:
select id,name,low_price,second_category_id,second_category_name,status,total_sales,update_time
from product

    merchant_id = #{merchant_id}
    
    and first_category_id = #{level1Category}
    
    
    and second_category_id = #{level2Category}
    
    
    and status = #{status}
    
    
    and name like concat('%',#{name},'%')
